import random
class Solution(object):

# 使用 `LC470. 用 Rand7() 实现 Rand10()` 中的拒绝采样思想。
# https://leetcode-cn.com/problems/implement-rand10-using-rand7/

    def __init__(self, m, n):
        """
        :type m: int
        :type n: int
        """
        self.mtx = [[0 for _ in range(n)] for _ in range(m)]
        self.m = m
        self.n = n

    def flip(self):
        """
        :rtype: List[int]
        """
        def generate_random_coordinate(x, y):
            return random.randint(0, x-1), random.randint(0, y-1)
        m, n = self.m, self.n
        while True:
            randX, randY = generate_random_coordinate(m, n)
            if self.mtx[randX][randY] == 0:
                self.mtx[randX][randY] = 1
                return [randX, randY]

    def reset(self):
        """
        :rtype: None
        """
        self.mtx = [[0 for _ in range(self.n)] for _ in range(self.m)]


