class Solution(object):
    def minimalKSum(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        
        n = len(nums)
        nums.sort()
        res = 0
        insertedNum = 1
        
        for i, num in enumerate(nums):
            while k > 0 and insertedNum < num:
                res += insertedNum
                insertedNum += 1
                k -= 1
            if k == 0:
                break
            else:
                insertedNum = num + 1
        if k > 0:
            res += sum(range(num+1, num+k+1))
        return res
    
